import React, { useEffect ,useState} from 'react'
import { useParams } from 'react-router-dom'
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
const Part = () => {
    const nav=useNavigate();
    const pp  = useParams(); // Extracts "pp" from URL params
    const dispatch=useDispatch();
    const val=pp.id
    const [data, setData] = useState(null);
    const [cityN, setcityN] = useState(null);
    const [score,setscore]=useState(0);
    const [newgame,setnewgame]=useState(0);
    const [anslvl,setanslvl]=useState(false);

    const selecter=useSelector
  
    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await fetch(`http://localhost:3005/query`);
          const result = await response.json(); // Parse JSON response
          const cityNames = result.map((m) => m.city); // Extract city names
          setData(result[val]);
          setcityN(cityNames)
          console.log(result)
        } catch (error) {
          console.error("Error fetching data:", error);
        }
      };
  
      fetchData();
    }, [pp,newgame]); // Dependency array ensures it runs when `pp` changes

    const Choosed=(a)=>{
        if(a==data.city){
            setscore((prev)=>{return (prev+10)})
            console.log("Correct ans choosen");
            setanslvl(true);

        }
        else{
            if(anslvl==false){
                setscore((prev)=>{return (prev+-10)})
            }
            console.log("Wrong ans choosen");
            setanslvl(false);
        }

    }
    const Replay=()=>{
        setscore(0);

    }
    const Nextpart=()=>{
        
        var lgth=11;
        var a=Math.floor(Math.random()*lgth);
        console.log(a);
        dispatch({type:'changed',val:score})
        setnewgame(prev=>{return prev+1});
        console.log(newgame,a);
        nav(`/part/${a}`)
    }
  
  return (
    <div>
        <h1>{pp.id}</h1>
        <ul>
            {data? data.clues.map((clue, index) => (
              <li key={index}>{clue}</li>
            )):<></>}
        </ul>

        <div>
            {cityN? cityN.map((m)=>(
                <button key={m}  onClick={()=>Choosed(m)}>{m}</button>
            )):<></>}
        </div>

        {anslvl==true?<div>
            <h2>Fun Facts about {data.city}</h2>
            {data.fun_fact.map((val, index) => (
              <li key={index}>{val}</li>
            ))}

            <h2>Trivia's : </h2>
            {data.trivia.map((val, index) => (
              <li key={index}>{val}</li>
            ))}


        </div>:<></>}


        <div>
            <button onCanPlay={Replay}>Play Again</button>
            <button onClick={Nextpart}>Next button</button>
        </div>


      
    </div>
  )
}

export default Part
