import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import Popup from "./Popup";
import Confetti from "react-confetti";
import useSound from "use-sound";
import correctSound from "./sounds/correct.mp3";
import wrongSound from "./sounds/wrong.mp3";
import "./Part.css";

const Part = () => {
  const nav = useNavigate();
  const { id } = useParams();
  const dispatch = useDispatch();
  
  const [data, setData] = useState(null);
  const [cityN, setCityN] = useState([]);
  const [score, setScore] = useState(0);
  const [anslvl, setAnslvl] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [correctAns, setCorrectAns] = useState(null);
  const [buttonPopup, setButtonPopup] = useState(false);
  const [wrongAttempts, setWrongAttempts] = useState(0);
  const [hintUsed, setHintUsed] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  
  const totalScore = useSelector((state) => state.totscore);
  
  const [playCorrect] = useSound(correctSound);
  const [playWrong] = useSound(wrongSound);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:3005/query`);
        const result = await response.json();
        
        const correctCity = result[id].city;
        let cityOptions = result
          .map((m) => m.city)
          .filter(city => city !== correctCity);
        
        cityOptions = cityOptions.sort(() => 0.5 - Math.random()).slice(0, 4);
        cityOptions.push(correctCity);
        cityOptions = cityOptions.sort(() => 0.5 - Math.random()); // Shuffle
        
        setData(result[id]);
        setCityN(cityOptions);
        setCorrectAns(correctCity);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    
    fetchData();
  }, [id]);

  const handleChoice = (answer) => {
    setSelectedAnswer(answer);
    if (answer === correctAns) {
      playCorrect();
      setScore((prev) => prev + 10 + (wrongAttempts === 0 ? 5 : 0)); // Bonus for first attempt
      setAnslvl(true);
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 2000);
    } else {
      playWrong();
      setScore((prev) => prev - 5);
      setAnslvl(false);
      setWrongAttempts((prev) => prev + 1);
    }
  };

  const handleReplay = () => {
    setSelectedAnswer(null);
    setScore(0);
    setWrongAttempts(0);
    setHintUsed(false);
  };

  const handleNext = () => {
    const newId = Math.floor(Math.random() * 11);
    dispatch({ type: "updated", val: score });
    setScore(0);
    setSelectedAnswer(null);
    setWrongAttempts(0);
    setHintUsed(false);
    nav(`/part/${newId}`);
  };

  const shareOnWhatsApp = () => {
    const gameLink = `https://yourgame.com/part/${id}`;
    const message = `Hey! I scored ${totalScore} points in this game. Can you beat me? Click here to play: ${gameLink}`;
    const whatsappURL = `https://api.whatsapp.com/send?text=${encodeURIComponent(message)}`;
    window.open(whatsappURL, "_blank");
  };

  const revealHint = () => {
    if (!hintUsed) {
      setHintUsed(true);
      alert(`Hint: The city's name starts with "${correctAns[0]}"`);
    }
  };

  return (
    <div className="container2">
      {showConfetti && <Confetti />}
      <h1 className="start">Guess the City Name</h1>
      
      <div>
        <h2 className="share">Share with a Friend</h2>
        <button onClick={() => setButtonPopup(true)}>Click to Share/Invite</button>
        <Popup trigger={buttonPopup} settrigger={setButtonPopup}>
          <h3>Challenge Your Friends!</h3>
          <button onClick={shareOnWhatsApp}>Share via WhatsApp</button>
        </Popup>
      </div>

      <ul className="list1">
        {data ? data.clues.slice(0, 3).map((clue, index) => <li key={index}>{clue}</li>) : <></>}
      </ul>
      
      <button className="hint-button" onClick={revealHint} disabled={hintUsed}>Get a Hint</button>
      
      <div>
        {cityN.map((option) => (
          <button
            key={option}
            className={`option-button ${selectedAnswer === option ? "selected" : ""}`}
            onClick={() => handleChoice(option)}
            disabled={selectedAnswer !== null}
            style={{
              backgroundColor: selectedAnswer === option ? (option === correctAns ? "green" : "red") : "#4CAF50",
            }}
          >
            {option}
          </button>
        ))}
      </div>
      
      {selectedAnswer && (
        <div>
          <h2>{anslvl ? `ðŸŽ‰ Fun Facts about ${data.city}` : "ðŸ˜¢ Oops! Try Again"}</h2>
          <ul className="list1">
            {data.fun_fact.slice(0, wrongAttempts + 1).map((val, index) => (
              <li key={index}>{val}</li>
            ))}
          </ul>
        </div>
      )}

      <div>
        <button onClick={handleReplay} className="replay-button">Play Again</button>
        <button onClick={handleNext} className="next-button" disabled={!anslvl}>Next</button>
      </div>

      <h1>Total Score: {totalScore}</h1>
    </div>
  );
};

export default Part;
